import React, { Component } from "react";
import Statistics from "../Statistics/Statistics";

class Feedback extends Component {
  static defaultProps = {
    step: 1,
    good: 0,
    neutral: 0,
    bad: 0,
  };
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
    isOpen: false,
  };

  plusGoodFeedback = () => {
    this.setState((state, props) => ({
      good: state.good + props.step,
    }));
  };
  plusNeutralFeedback = () => {
    this.setState((state, props) => ({
      neutral: state.neutral + props.step,
    }));
  };

  plusBadFeedback = () => {
    this.setState((state, props) => ({
      bad: state.bad + props.step,
    }));
  };

  render() {
    return (
      <div>
        <p>Пожалуйста оставте свой отзыв</p>
        <div>
          <button type="button" onClick={this.plusGoodFeedback}>
            Отлично
          </button>
          <button type="button" onClick={this.plusNeutralFeedback}>
            Средне
          </button>
          <button type="button" onClick={this.plusBadFeedback}>
            Плохо
          </button>
        </div>
        <div>
          <Statistics />
        </div>
      </div>
    );
  }
}

export default Feedback;
