import React, { Component } from "react";

class Statistics extends Component {
  static defaultProps = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  render() {
    return (
      <div>
        <p>Статистика</p>
        <ul>
          <li>
            <span>Отлично</span>
            <span>{this.props.statistics.good}</span>
          </li>
          <li>
            <span>Средне</span>
            <span>{this.props.statistics.neutral}</span>
          </li>
          <li>
            <span>Плохо</span>
            <span>{this.props.statistics.bad}</span>
          </li>
          <li>
            <span>Всего</span>
            <span>{this.props.statistics.totall}</span>
          </li>
        </ul>
      </div>
    );
  }
}

export default Statistics;
